import React from "react";
import TestimonialCard from "./TestimonialCard";
import Heading from "../../../components/heading/Heading";
import "./TestimonialSection.css";
import { useEffect, useState } from "react";

function TestimonialSection() {
  let [medioType, setmedioType] = useState("pc");
  useEffect(() => {
    if(checkMedio() == null){
        setmedioType('pc')
    }else{
        setmedioType('phone')
    }
  }, []);
  const checkMedio = () => {
    const userAgent = navigator.userAgent;
    return userAgent.match(
      /(iPhone|iPod|Android|ios|iPad|AppleWebKit.*Mobile.*)/i
    );
  };
  return (
    <div className="testimonialSection">
      <Heading name="Testimony" />
      <div className={medioType === 'pc' ? "displayTestimony" : "displayTestimonyPhone"}>
        <TestimonialCard
          testimony="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut"
          userImg={require("../../../assets/images/user1.png")}
          userName="John Doe"
        />
        <TestimonialCard
          testimony="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut"
          userImg={require("../../../assets/images/user2.png")}
          userName="Jane Fibbs"
        />
      </div>
    </div>
  );
}

export default TestimonialSection;

